class Solution:
    def minCost(self, nums: List[int], x: int) -> int:
        n = len(nums)
        min_arrays = []

        for j in range(n):
            min_arr = [0] * n
            min_arr[0] = nums[j]
            for k in range(1, n):
                prev = (j - k) % n
                min_arr[k] = min(min_arr[k-1], nums[prev])
