import bisect

class SegmentTree:
    def __init__(self, size):
        self.n = size
        self.size_tree = 1
        while self.size_tree < self.n:
            self.size_tree <<= 1
        self.data = [-float('inf')] * (2 * self.size_tree)

    def update(self, pos, value):
        pos += self.size_tree
        if self.data[pos] >= value:
            return
        self.data[pos] = value
        while pos > 1:
            pos >>= 1
            new_val = max(self.data[2 * pos], self.data[2 * pos + 1])
            if self.data[pos] == new_val:
                break
            self.data[pos] = new_val

    def query(self, l, r):
        res = -float('inf')
        l += self.size_tree
        r += self.size_tree
        while l <= r:
            if l % 2 == 1:
                res = max(res, self.data[l])
                l += 1
            if r % 2 == 0:
                res = max(res, self.data[r])
                r -= 1
            l >>= 1
            r >>= 1
        return res if res != -float('inf') else -1

