    def canReachCorner(self, xCorner: int, yCorner: int, circles: List[List[int]]) -> bool:
        # Check if start (0,0) is inside any circle
        for x, y, r in circles:
            if x * x + y * y <= r * r:
                return False

        # Check if end (xCorner, yCorner) is inside any circle
        for x, y, r in circles:
            dx = xCorner - x
            dy = yCorner - y
            if dx * dx + dy * dy <= r * r:
                return False

        # Filter circles that intersect the rectangle
        filtered = []
        for x, y, r in circles:
            dx = max(x - xCorner, -x, 0)
            dy = max(y - yCorner, -y, 0)
            dist_sq = dx * dx + dy * dy
            if dist_sq <= r * r:
                filtered.append((x, y, r))

        n = len(filtered)
        if n == 0:
            return True

        # Union-Find setup
        parent = list(range(n))

        def find(u):
            while parent[u] != u:
                parent[u] = parent[parent[u]]  # Path compression
                u = parent[u]
            return u

        def union(u, v):
            pu = find(u)
            pv = find(v)
            if pu != pv:
                parent[pv] = pu  # Union by linking

        # Connect overlapping circles
        for i in range(n):
            xi, yi, ri = filtered[i]
            for j in range(i + 1, n):
                xj, yj, rj = filtered[j]
                dx = xi - xj
                dy = yi - yj
                dist_sq = dx * dx + dy * dy
                sum_r = ri + rj
                if dist_sq <= sum_r * sum_r:
                    union(i, j)

        # Check components for left-right or top-bottom connections
        component_info = {}
        for i in range(n):
            xi, yi, ri = filtered[i]
            left = (xi - ri) <= 0
            right = (xi + ri) >= xCorner
            top = (yi + ri) >= yCorner
            bottom = (yi - ri) <= 0
            root = find(i)
            if root not in component_info:
                component_info[root] = [left, right, top, bottom]
            else:
                component_info[root][0] |= left
                component_info[root][1] |= right
                component_info[root][2] |= top
                component_info[root][3] |= bottom

        for flags in component_info.values():
            left, right, top, bottom = flags
            if (left and right) or (top and bottom):
                return False

        return True

class Solution:
    def numberOfSubstrings(self, s: str) -> int:
        zeros = [i for i, c in enumerate(s) if c == '0']
        n = len(s)
        total = 0

        # Calculate all-ones substrings
        prev = -1
        for i in range(n + 1):
            if i < n and s[i] == '1':
                continue
            else:
                m = i - prev - 1
                total += m * (m + 1) // 2
                prev = i

        if not zeros:
            return total

        max_k = 200  # Empirical upper bound to cover all possible cases

        for k in range(1, max_k + 1):
            required_length = k * k + k
            if required_length > n:
                continue
            num_groups = len(zeros) - k + 1
            if num_groups <= 0:
                continue

            for i in range(num_groups):
                # Calculate start range [s_start, s_end]
                if i == 0:
                    s_start = 0
                else:
                    s_start = zeros[i - 1] + 1
                s_end = zeros[i]

                # Calculate end range [e_start, e_end]
                e_start = zeros[i + k - 1]
                if i + k < len(zeros):
                    e_end = zeros[i + k] - 1
                else:
                    e_end = n - 1

                # Check if start and end ranges are valid
                if s_end < s_start or e_end < e_start:
                    continue

                a = max(e_start, required_length + s_start - 1)
                b_val = e_end
                if a > b_val:
                    continue

                a_prime = a - required_length + 1
                b_prime = b_val - required_length + 1

                # Compute sum_min for e_prime in [a_prime, b_prime]
                s_end_val = s_end
                if a_prime > b_prime:
                    continue  # No valid terms
                if a_prime > s_end_val:
                    sum_min = s_end_val * (b_prime - a_prime + 1)
                elif b_prime < s_end_val:
                    sum_min = (a_prime + b_prime) * (b_prime - a_prime + 1) // 2
                else:
                    part1_end = s_end_val
                    part1_length = part1_end - a_prime + 1
                    sum_part1 = (a_prime + part1_end) * part1_length // 2
                    part2_length = b_prime - part1_end
                    sum_part2 = s_end_val * part2_length
                    sum_min = sum_part1 + sum_part2

                num_terms = b_prime - a_prime + 1
                current = sum_min - (s_start - 1) * num_terms
                total += current

        return total

