import bisect

class Solution:
    def validSubstringCount(self, word1: str, word2: str) -> int:
        need = Counter(word2)
        m = len(word2)
        n = len(word1)

        # Check if word1 has enough characters to satisfy word2's needs
        total_counts = Counter(word1)
        for c in need:
            if total_counts.get(c, 0) < need[c]:
                return 0

        # Precompute prefix sums for each character in need
        prefix = {c: [0] * (n + 1) for c in need}
        for c in need:
            count = 0
            for i in range(n):
                if word1[i] == c:
                    count += 1
                prefix[c][i+1] = count

        ans = 0
        # Iterate over all possible end indices of the substring in word1
        for right in range(m - 1, n):
            valid = True
            K = {}
            # Calculate K_c for each character in need
            for c in need:
                current = prefix[c][right + 1]
                required = need[c]
                K[c] = current - required
                if K[c] < 0:
                    valid = False
                    break
            if not valid:
                continue

            # Find the upper bounds for each character's prefix sum
            upper_c = []
            for c in need:
                # Find the largest index i where prefix[c][i] <= K[c]
                idx = bisect.bisect_right(prefix[c], K[c]) - 1
                upper_c.append(idx)

            min_upper = min(upper_c)
            max_i = min(min_upper, right - m + 1)
            if max_i >= 0:
                ans += (max_i + 1)

        return ans

class Solution:
    def maxValue(self, nums: List[int], k: int) -> int:
        n = len(nums)

        # Compute left_ORs: OR values for selecting k elements from the first i+1 elements
        left_ORs = []
        prev_dp = [set() for _ in range(k + 1)]
        prev_dp[0].add(0)
        for num in nums:
            new_dp = [set() for _ in range(k + 1)]
            for c in range(k + 1):
                new_dp[c] = set(prev_dp[c])  # Copy previous state without current num
            for c in range(1, k + 1):
                for or_val in prev_dp[c - 1]:
                    new_dp[c].add(or_val | num)
            prev_dp = new_dp
            left_ORs.append(prev_dp[k].copy())

        # Compute right_ORs: OR values for selecting k elements from elements starting at index i to the end
        right_ORs = [set() for _ in range(n)]
        prev_dp = [set() for _ in range(k + 1)]
        prev_dp[0].add(0)
        for i in range(n - 1, -1, -1):
            num = nums[i]
            new_dp = [set() for _ in range(k + 1)]
            for c in range(k + 1):
                new_dp[c] = set(prev_dp[c])  # Copy previous state without current num
            for c in range(1, k + 1):
                for or_val in prev_dp[c - 1]:
                    new_dp[c].add(or_val | num)
            prev_dp = new_dp
            right_ORs[i] = prev_dp[k].copy()

        max_val = 0
        # Iterate over all valid split points m
        for m in range(k - 1, n - k):
            left = left_ORs[m]
            right = right_ORs[m + 1]
            if not left or not right:
                continue
            current_max = max(a ^ b for a in left for b in right)
            if current_max > max_val:
                max_val = current_max

        return max_val

