class Solution:
    def minDifference(self, nums: List[int]) -> int:
        known = []
        n = len(nums)
        for i in range(n):
            if nums[i] != -1:
                known.append((i, nums[i]))

        if not known:
            return 0

        initial_max = 0
        for i in range(1, n):
            if nums[i-1] != -1 and nums[i] != -1:
                initial_max = max(initial_max, abs(nums[i] - nums[i-1]))

