            b_idx, b_val = known[i]
            diff = abs(b_val - a_val)
            distance = b_idx - a_idx
            if distance == 0:
                continue  # Consecutive elements, no gap
            required = diff // distance
            if diff % distance != 0:
                required += 1
            max_required = max(max_required, required)

