import sys

def get_coords(poly):
    coords = []
    for i in range(4):
        for j in range(4):
            if poly[i][j] == '#':
                coords.append((i, j))
    return coords

def normalize(coords):
    if not coords:
        return []
    min_i = min(c[0] for c in coords)
    min_j = min(c[1] for c in coords)
    return [(i - min_i, j - min_j) for (i, j) in coords]

def generate_unique_shapes(coords):
    if not coords:
        return set()
    original = normalize(coords)
    unique = set()
    for angle in [0, 90, 180, 270]:
        rotated = []
        for (i, j) in original:
            if angle == 0:
                ni, nj = i, j
            elif angle == 90:
                ni, nj = j, -i
            elif angle == 180:
                ni, nj = -i, -j
