import sys
from collections import defaultdict

def main():
    input = sys.stdin.read().split()
    n = int(input[0])
    events = []
    potions = defaultdict(list)
    monsters = defaultdict(list)
    idx = 1
    for i in range(n):
        t = int(input[idx])
        x = int(input[idx+1])
        events.append((t, x))
        if t == 1:
            potions[x].append(i + 1)  # 1-based index
        else:
            monsters[x].append(i + 1)
        idx += 2

    assigned = set()
    possible = True

    for x in monsters:
        ms = monsters[x]
        ps = potions[x]
        if len(ps) < len(ms):
            possible = False
            break
        j = len(ps) - 1
        for m in reversed(ms):
            while j >= 0 and ps[j] >= m:
                j -= 1
            if j >= 0:
                assigned.add(ps[j])
                j -= 1
            else:
                possible = False
                break
        if not possible:
            break

    if not possible:
        print(-1)
        return

    choices = []
    for i in range(n):
        t, x = events[i]
        if t == 1:
            event_idx = i + 1
            choices.append(1 if event_idx in assigned else 0)

    max_k = 0
    current = 0
    for i in range(n):
        t, x = events[i]
        if t == 1:
            event_idx = i + 1
            if event_idx in assigned:
                current += 1
                if current > max_k:
                    max_k = current
        else:
            current -= 1

    print(max_k)
    print(' '.join(map(str, choices)))

if __name__ == "__main__":
    main()

import sys
from collections import deque

def main():
    MOD = 998244353
    input = sys.stdin.read
    data = input().split()
    ptr = 0
    H = int(data[ptr])
    ptr += 1
    W = int(data[ptr])
    ptr += 1
    grid = []
    for _ in range(H):
        grid.append(data[ptr])
        ptr += 1

    comp_id = [[0] * W for _ in range(H)]
    C_initial = 0
    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]

    for i in range(H):
        for j in range(W):
            if grid[i][j] == '#' and comp_id[i][j] == 0:
                queue = deque()
                queue.append((i, j))
                comp_id[i][j] = C_initial + 1
                while queue:
                    x, y = queue.popleft()
                    for dx, dy in directions:
                        nx = x + dx
                        ny = y + dy
                        if 0 <= nx < H and 0 <= ny < W:
                            if grid[nx][ny] == '#' and comp_id[nx][ny] == 0:
                                comp_id[nx][ny] = C_initial + 1
                                queue.append((nx, ny))
                C_initial += 1

    R = sum(row.count('.') for row in grid)
    total = 0
    for i in range(H):
        for j in range(W):
            if grid[i][j] == '.':
                adjacent_comps = set()
                for dx, dy in directions:
                    x = i + dx
                    y = j + dy
                    if 0 <= x < H and 0 <= y < W:
                        if grid[x][y] == '#':
                            adjacent_comps.add(comp_id[x][y])
                k = len(adjacent_comps)
                total += (C_initial + 1 - k)

    inv_R = pow(R, MOD - 2, MOD)
    ans = (total * inv_R) % MOD
    print(ans)

if __name__ == "__main__":
    main()

