    if m < 2:
        return []
    sieve = [True] * (m + 1)
    sieve[0] = sieve[1] = False
    for i in range(2, int(m**0.5) + 1):
        if sieve[i]:
            sieve[i*i : m+1 : i] = [False] * len(sieve[i*i : m+1 : i])
    return [i for i, is_p in enumerate(sieve) if is_p]

def main():
    import sys
    input = sys.stdin.read
    data = input().split()
    N = int(data[0])
    M = int(data[1])

    mod = 998244353

    primes = primes_upto(M)
    prime_count = len(primes)

    if prime_count == 0:
        # All elements are 1, divisor count for each is 1. Sum is N mod mod
        print(N % mod)
        return

    # Factorize each x in 1..M
    prime_factors = []
    for x in range(1, M + 1):
        factors = [0] * prime_count
        temp = x
        for idx, p in enumerate(primes):
            while temp % p == 0:
                factors[idx] += 1
                temp //= p
        prime_factors.append(factors)

    subsets = 1 << prime_count
    c = [0] * subsets

    for x in range(M):
        exponents = prime_factors[x]
        for T in range(subsets):
            product = 1
            for p in range(prime_count):
                if (T >> p) & 1:
                    product *= exponents[p]
            c[T] = (c[T] + product) % mod

    size = subsets
    # Build matrix A
    A = [[0] * size for _ in range(size)]
    for S in range(size):
        for R in range(size):
            if (R & S) == R:
                T = S ^ R
                A[S][R] = c[T] % mod
            else:
                A[S][R] = 0

