class Solution:
    def minOperations(self, nums: List[int], target: int) -> int:
        counts = [0]*31  # counts of 2^i

        for num in nums:
            e = num.bit_length() - 1  # since nums[i] is power of two
            counts[e] +=1

        required = [(target >> i) &1 for i in range(31)]

        ans = 0
        for i in range(31):
            # First, ensure counts[i] are enough for required[i]
            if counts[i] >= required[i]:
                # We can pass excess counts to next bit
                pass
            else:
                # We need to make up deficit counts
                deficit = required[i] - counts[i]
                # Find higher bits to split
                j = i + 1
                while j < 31 and counts[j] == 0:
                    j +=1
                if j == 31:
                    return -1  # impossible to satisfy target
                # Split counts[j] down to counts[i]
                for k in range(j, i, -1):
                    counts[k] -=1
                    counts[k -1] +=2
                    ans +=1  # one operation per split
                counts[i] +=1  # Now counts[i] has at least required[i]

            # Pass excess counts to next bit if any
            excess = counts[i] - required[i]
            if i +1 <31:
                counts[i+1] += excess //2

        return ans

class Solution:
    def minOperations(self, s1: str, s2: str, x: int) -> int:
        n = len(s1)
        mismatches = []
        for i in range(n):
            if s1[i] != s2[i]:
                mismatches.append(i)
        M = len(mismatches)
        if M % 2 != 0:
            return -1
        total_cost = 0
        i = 0
        isolated_mismatches = []
        while i < M:
            run_start = i
            # Find the length of the run of mismatches
            while i + 1 < M and mismatches[i + 1] == mismatches[i] +1:
                i +=1
            run_end = i
            L = run_end - run_start +1
            if L ==1:
                isolated_mismatches.append(mismatches[i])
            elif L % 2 == 0:
                total_cost += (L //2) *1
            else:  # L >=3 and odd
                total_cost += ((L -2) // 2)*1 + x
            i +=1
        # Pair up isolated mismatches
        K = len(isolated_mismatches)
        if K %2 != 0:
            return -1
        total_cost += (K //2) * x
        return total_cost

