class Solution:
    def minimumOperationsToMakeEqual(self, x: int, y: int) -> int:
        from collections import deque

        max_limit = 20000  # Assuming y can be up to 10000, we can double it to be safe
        visited = set()
        queue = deque()
        queue.append((x, 0))
        visited.add(x)

        while queue:
            curr_x, steps = queue.popleft()
            if curr_x == y:
                return steps
            next_steps = steps + 1
            # Operation 1: Divide x by 11 if x is a multiple of 11
            if curr_x % 11 == 0:
                next_x = curr_x // 11
                if next_x not in visited and 1 <= next_x <= max_limit:
                    visited.add(next_x)
                    queue.append((next_x, next_steps))
            # Operation 2: Divide x by 5 if x is a multiple of 5
            if curr_x % 5 == 0:
                next_x = curr_x // 5
                if next_x not in visited and 1 <= next_x <= max_limit:
                    visited.add(next_x)
                    queue.append((next_x, next_steps))
            # Operation 3: Decrement x by 1
            if curr_x - 1 >= 1:
                next_x = curr_x - 1
                if next_x not in visited:
                    visited.add(next_x)
                    queue.append((next_x, next_steps))
            # Operation 4: Increment x by 1
            if curr_x + 1 <= max_limit:
                next_x = curr_x + 1
                if next_x not in visited:
                    visited.add(next_x)
                    queue.append((next_x, next_steps))

        # If we reach here, then y is not reachable from x (should not happen given constraints)
        return -1

class Solution:
    def findMaximumNumber(self, k: int, x: int) -> int:
        def compute_total_price(n, x):
            total_price = 0
            maxbit = n.bit_length()
            for k in range(maxbit):
                if (k + 1) % x == 0:
                    cycles = (n + 1) // (1 << (k + 1))
                    ones_in_full_cycles = cycles * (1 << k)
                    remainder = (n + 1) % (1 << (k + 1))
                    ones_in_remainder = max(0, remainder - (1 << k))
                    ones_in_remainder = min(ones_in_remainder, (1 << k))
                    total_ones_at_k = ones_in_full_cycles + ones_in_remainder
                    total_price += total_ones_at_k
            return total_price

        left, right = 1, 10**17
        answer = 0
        while left <= right:
            mid = (left + right) // 2
            total_price = compute_total_price(mid, x)
            if total_price <= k:
                answer = mid
                left = mid + 1
            else:
                right = mid - 1
        return answer

