        for num in arr2:
            node = root
            num_str = str(num)
            current_length = 0
            for ch in num_str:
                idx = int(ch)
                if node.children[idx]:
                    node = node.children[idx]
                    current_length += 1
                    max_length = max(max_length, current_length)
                else:
                    break  # No further match
        return max_length
