    def minAnagramLength(self, s: str) -> int:
        from math import isqrt
        n = len(s)
        divisors = set()
        for i in range(1, isqrt(n)+1):
            if n % i == 0:
                divisors.add(i)
                divisors.add(n // i)
        divisors = sorted(divisors)
        s_list = list(s)
        for k in divisors:
