                        prev_peak = peaks[i]
                        if nums[i] > nums[i-1] and nums[i] > nums[i+1]:
                            peaks[i] =1
                        else:
                            peaks[i]=0
                        if peaks[i] != prev_peak:
                            tree.update(i, peaks[i])
        return res

