class Solution:
    def getSmallestString(self, s: str) -> str:
        s = list(s)
        n = len(s)
        for i in range(n - 1):
            if int(s[i]) % 2 == int(s[i + 1]) % 2:
                if s[i] > s[i + 1]:
                    s[i], s[i + 1] = s[i + 1], s[i]
        return ''.join(s)
