    k = 0
    for i in range(N):
        for j in range(i+1,N):
            D[i][j] = edge_weights[k]
            k +=1
    memo = {}
    def dfs(mask):
        if mask == 0:
            return 0
        if mask in memo:
            return memo[mask]
        max_value = 0
        # Find the first set bit i
        for i in range(N):
            if mask & (1<<i):
                break
        for j in range(i+1,N):
            if mask & (1<<j):
                next_mask = mask ^ (1<<i) ^ (1<<j)
                value = dfs(next_mask) + D[i][j]
                if value > max_value:
                    max_value = value
        memo[mask] = max_value
        return max_value
    full_mask = (1<<N)-1
    result = dfs(full_mask)
    print(result)
