            if i == 0:   # 0 degrees
                nx, ny = x, y
            elif i == 1:  # 90 degrees
                nx, ny = -y, x
            elif i == 2:  # 180 degrees
                nx, ny = -x, -y
            elif i == 3:  # 270 degrees
                nx, ny = y, -x
            new_cells.add((nx, ny))
        # Normalize positions to start from (0, 0)
        min_x = min(x for x, y in new_cells)
        min_y = min(y for x, y in new_cells)
