            if c == X[state]:
                next_state = state +1
                if next_state == M:
                    # Reached the forbidden sequence, avoid this transition
                    next_state = -1
            else:
                next_state = state
                while next_state > 0 and c != X[next_state]:
                    next_state = fail[next_state-1]
                if c == X[next_state]:
                    next_state +=1
                # If next_state == M, avoid transition
                if next_state == M:
